  border-radius: 5px;
  font-size: 1.2rem;
  font-weight: bold;
  cursor: pointer;
  text-decoration: none;
}

/* Layout */
.container-flex {
  display: flex;
  width: 100%;

  flex-direction: row;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;

  margin-right: auto;
  margin-left: auto;
