  background-color: #ff5a5f;
  color: #fff;
  padding: 5px 10px;
  border-radius: 5px;
  font-size: 15px;
  text-transform: uppercase;
  position: absolute;
  top: 10px;
}
.post-info {
  display: flex;
  justify-content: flex-start;
  align-items: center;
  color: gray;
  font-size: 1.2rem;
}

.post-content h1 {
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 10px;
  color: #062e83;
}

.post-author a {
  text-decoration: none;
  color: #062e83;
  margin-right: 10px;
}
/* Footer */
footer {
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  padding: 10px;
  background-color: #003049;
  color: #ffff;
  margin-top: 30px;
}

footer .about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

footer p {
  color: #8d99ae;
}

.logo-text {
  font-size: 4.5rem;
  font-weight: bold;
  color: #ffbe0b;
}

footer .contact {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 10px;
  color: #fff;
  margin-bottom: 1px;
  font-size: 1.5rem;
}

footer .contact span {
  padding: 10px;
}
footer .contact i {
  padding: 10px;
  font-size: 30px;
}

footer .contact a {
  text-decoration: none;
  font-size: 30px;
  color: #0077b6;
}

footer .socials a {
  margin: 10px;
  font-size: 30px;
  color: #fcbf49;
  text-decoration: none;
}
/* Mobile first responsive design */
@media (min-width: 675px) {
  nav {
    flex-direction: row;
    justify-content: space-between;
  }
  nav ul {
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    margin-right: 20px;
  }
  .container-flex {
    flex-direction: row;
  }
  nav ul li {
    padding: 10px;
  }
  main {
    width: 70%;
  }
  aside {
    width: 30%;
  }
}
